targets.expr:
(
  sum by (container) (
    rate(container_cpu_usage_seconds_total{namespace=~"[[namespace]]", cluster=~"[[cluster]]", pod=~"[[pod_name]]", node=~"[[node_name]]", image!="", pod=~"[[pod_name]]", container!="POD", container!="", container=~"[[container_name]]"}[5m])
  ) > 0.5
) 
/
(
  sum by (container) (
    cluster:namespace:pod_cpu:active:kube_pod_container_resource_requests{namespace=~"[[namespace]]", cluster=~"[[cluster]]", pod=~"[[pod_name]].*", node=~"[[node_name]]", container!="POD", container!="", container=~"[[container_name]]"}
  )
) > 1
